```c
#include <stdio.h>
#include <stdlib.h>
#define ELEMTYPE int
#define MAXSIZE 100

static int top = -1;
//Struct of binary tree node
typedef struct BiTNode {
	ELEMTYPE data;
	struct BiTNode *lchild, *rchild;//child node pointer
}BiTNode, *BiTree;

/**
*Function:	create a tree
*Input:		Bitree
*return
*/
void CreateTree(BiTree *T) {
	printf("Tree Graph:\n");
	printf("1:       1		\n");
	printf("        /\\		\n");
	printf("2:     2  3		\n");
	printf("      /\\  /\\ 	\n");
	printf("3:   4 5  6 7	\n");

	//Depth: 1
	*T = (BiTNode*)malloc(sizeof(BiTNode));
	(*T)->data = 1;

	//Depth: 2
	(*T)->lchild = (BiTNode*)malloc(sizeof(BiTNode));
	(*T)->lchild->data = 2;

	(*T)->rchild = (BiTNode*)malloc(sizeof(BiTNode));
	(*T)->rchild->data = 3;

	//Depth: 3
	(*T)->lchild->lchild = (BiTNode*)malloc(sizeof(BiTNode));
	(*T)->lchild->lchild->data = 4;

	(*T)->lchild->rchild = (BiTNode*)malloc(sizeof(BiTNode));
	(*T)->lchild->rchild->data = 5;

	(*T)->rchild->lchild = (BiTNode*)malloc(sizeof(BiTNode));
	(*T)->rchild->lchild->data = 6;

	(*T)->rchild->rchild = (BiTNode*)malloc(sizeof(BiTNode));
	(*T)->rchild->rchild->data = 7;

	//Depth: 4
	(*T)->lchild->lchild->lchild = NULL;
	(*T)->lchild->lchild->rchild = NULL;

	(*T)->lchild->rchild->lchild = NULL;
	(*T)->lchild->rchild->rchild = NULL;

	(*T)->rchild->lchild->lchild = NULL;
	(*T)->rchild->lchild->rchild = NULL;

	(*T)->rchild->rchild->lchild = NULL;
	(*T)->rchild->rchild->rchild = NULL;

}
/**
*Function:	visit a node
*Input:		BiTNode
*return
*/
void Visit(BiTNode *T) {
	printf("Preorder:%d\n", T->data);
}
/**
*Function:	preorder visit a tree
*Input:		Bitree
*return
*/
void Preorder(BiTree T) {
	if (T) {
		Visit(T);
		Preorder(T->lchild);
		Preorder(T->rchild);
	}
	return;
}
/**
*Function:	pop
*Input:		BiTNode*[]
*return		BiTNode*--The top element of stack.
*/
BiTNode* pop(BiTNode* Stack[]) {
	BiTNode* a = Stack[top--];
	return a;
}
/**
*Function:	push
*Input:		BiTNode*[], BiTNode*
*return
*/
void push(BiTNode* Stack[], BiTNode* a) {
	Stack[++top] = a;
}
/**
*Function:	preorder visit a tree by using a stack
*Input:		Bitree
*return
*/
void PreorderStack(BiTree T) {
	BiTNode* Stack[MAXSIZE];//Initialize the stack
	while (T) {
		Visit(T);
		if (T->lchild) {//If T has left child
			if (T->rchild) {	//If T has left and right child
				push(Stack,T->rchild);
			}
			T = T->lchild;
		}
		else if(T->rchild){//If T only has right child
			T = T->rchild;
		}
		else if(top>=0){//If Stack has elements
			BiTNode *a = pop(Stack);
			T = a;
		}
		else {//If T is last node in preorder.
			break;
		}
	}


	return;
}
int main() {
	BiTree tree;
	CreateTree(&tree);
	//recursive
	printf("Recursive PreOrder\n");
	Preorder(tree);

	//not recursive
	printf("Non-recursive PreOrder\n");
	PreorderStack(tree);
	getchar();
	return 0;
}
```


