#include <stdio.h>
#include <stdlib.h>
#define ELEMTYPE int
#define MAXSIZE 100

static int front = 0, rear = 0;
//Struct of binary tree node
typedef struct BiTNode {
	ELEMTYPE data;
	struct BiTNode *lchild, *rchild;//child node pointer
}BiTNode, *BiTree;

/**
*Function:	create a tree
*Input:		Bitree
*return
*/
void CreateTree(BiTree *T) {
	printf("Tree Graph:\n");
	printf("1:       1		\n");
	printf("        /\\		\n");
	printf("2:     2  3		\n");
	printf("      /\\  /\\ 	\n");
	printf("3:   4 5  6 7	\n");

	//Depth: 1
	*T = (BiTNode*)malloc(sizeof(BiTNode));
	(*T)->data = 1;

	//Depth: 2
	(*T)->lchild = (BiTNode*)malloc(sizeof(BiTNode));
	(*T)->lchild->data = 2;

	(*T)->rchild = (BiTNode*)malloc(sizeof(BiTNode));
	(*T)->rchild->data = 3;

	//Depth: 3
	(*T)->lchild->lchild = (BiTNode*)malloc(sizeof(BiTNode));
	(*T)->lchild->lchild->data = 4;

	(*T)->lchild->rchild = (BiTNode*)malloc(sizeof(BiTNode));
	(*T)->lchild->rchild->data = 5;

	(*T)->rchild->lchild = (BiTNode*)malloc(sizeof(BiTNode));
	(*T)->rchild->lchild->data = 6;

	(*T)->rchild->rchild = (BiTNode*)malloc(sizeof(BiTNode));
	(*T)->rchild->rchild->data = 7;

	//Depth: 4
	(*T)->lchild->lchild->lchild = NULL;
	(*T)->lchild->lchild->rchild = NULL;

	(*T)->lchild->rchild->lchild = NULL;
	(*T)->lchild->rchild->rchild = NULL;

	(*T)->rchild->lchild->lchild = NULL;
	(*T)->rchild->lchild->rchild = NULL;

	(*T)->rchild->rchild->lchild = NULL;
	(*T)->rchild->rchild->rchild = NULL;

}
/**
*Function:	visit a node
*Input:		BiTNode
*return
*/
void Visit(BiTNode *T) {
	printf("Hierarchical traversal:%d\n", T->data);
}

void EnQueue(BiTNode * queue[],BiTNode * a) {
	queue[rear++] = a;
}
BiTNode * DeQueue(BiTNode * queue[]) {
	BiTNode *b = queue[front++];
	return b;
}
/**
*Function:	Hierarchical traversal
*Input:		Bitree
*return
*/
void Hierarchical(const BiTree T) {
	BiTNode* Queue[MAXSIZE];
	BiTree q = T;
	EnQueue(Queue,q);
	while (front<rear) {
		BiTNode* node = DeQueue(Queue);
		Visit(node);
		if (node->lchild!=NULL) {
			EnQueue(Queue, node->lchild);
		}
		if (node->rchild != NULL) {
			EnQueue(Queue, node->rchild);
		}
	}
	return;
}

int main() {
	BiTree tree;
	CreateTree(&tree);
	//Hierarchical traversal
	Hierarchical(tree);
	getchar();
	delete tree;
	return 0;
}
