#include <stdio.h>
#include <stdlib.h>
#define ELEMTYPE int

//Struct of binary tree node
typedef struct BiTNode {
	ELEMTYPE data;
	struct BiTNode *lchild, *rchild;//child node pointer
	struct BiTNode *parent;//parent node pointer
}BiTNode, *BiTree;

/**
*Function:	create a tree
*Input:		Bitree
*return
*/
void createTree(BiTree *T) {
	printf("Tree Graph:\n");
	printf("1:       1		\n");
	printf("        /\\		\n");
	printf("2:     2  3		\n");
	printf("      /			\n");
	printf("3:   4			\n");

	//Depth: 1
	*T = (BiTNode*)malloc(sizeof(BiTNode));
	(*T)->data = 1;

	//Depth: 2
	(*T)->lchild = (BiTNode*)malloc(sizeof(BiTNode));
	(*T)->lchild->data = 2;
	(*T)->lchild->parent = *T;

	(*T)->rchild = (BiTNode*)malloc(sizeof(BiTNode));
	(*T)->rchild->data = 3;
	(*T)->rchild->parent = *T;

	//Depth: 3
	(*T)->lchild->lchild = (BiTNode*)malloc(sizeof(BiTNode));
	(*T)->lchild->lchild->data = 4;
	(*T)->lchild->lchild->parent = (*T)->lchild;

	(*T)->lchild->rchild = NULL;
	(*T)->rchild->lchild = NULL;
	(*T)->rchild->rchild = NULL;

	//Depth: 4
	(*T)->lchild->lchild->lchild = NULL;
	(*T)->lchild->lchild->rchild = NULL;

}

int main() {
	BiTree tree;
	createTree(&tree);
	printf("Root is:%d\n",tree->data);
	printf("Parent of the fourth node is:%d\n", tree->lchild->lchild->parent->data);
	getchar();
	return 0;
}
```


